import { Component, OnInit } from '@angular/core';
import { FormGroup, FormControl , Validators} from '@angular/forms';

@Component({
  selector: 'app-landing',
  templateUrl: './landing.component.html',
  styleUrls: ['./landing.component.css']
})
export class LandingComponent implements OnInit{
  LogoImages = './assets/images/LogoAseguradora.png';
  Back1Images = './assets/images/BannerFondo.jpeg';
  Back2Images = './assets/images/Back2Image.jpg';
  AsistenciaImage = './assets/images/AsistenciaImage.jpg';
  CorreoImage = './assets/images/CorreoImage.png';

  form!: FormGroup;

  constructor() { }

  ngOnInit() {
    this.form = new FormGroup({
      nombre: new FormControl(),
      correo: new FormControl(),
      contraseña: new FormControl()
    });
  }

  formUser = new FormGroup({
      'placa' : new FormControl('', [Validators.required]),
      'nombre': new FormControl('', [Validators.required]),
      'apellido': new FormControl('', [Validators.required]),
      'correo': new FormControl('', [Validators.required]),
      'telefono' : new FormControl('', [Validators.required]),

  });

  onSubmit() {
    console.log(this.formUser.value);
  }

}
<html>
  <body>
    <header>

      <div class="logo">
        <img src="{{LogoImages}}" alt="logo" />
      </div>

      <nav>
        <ul class="menu">
          <li><a href="#"><span class="Asistencia Icon"><img src="{{AsistenciaImage}}" ></span>Asistencia</a></li>
          <li><a href="#"><span class="Correo Icon"><img src="{{CorreoImage}}" ></span> Contactenos</a></li>
        </ul>
      </nav>
    </header>

    <form [formGroup]="formUser" class="formulario">
      <input type="text" formControlName="placa">
      <input type="email" formControlName="correo">
      <input type="text" formControlName="nombre">
      <input type="text" formControlName="apellido">
      <input type="text" formControlName="telefono">
      <button type="submit" (click)="onSubmit()">Registrarse</button>
    </form>

    <div class="Back1">
      <img src="{{Back1Images}}" alt="Back1">
    </div>

    <div class="Back2">
      <img src="{{Back2Images}}" alt="Back2">
    </div>

    <div class="video">
      <p>Video Comming Soon</p>
    </div>
  </body>
</html>
